import { useMemo, useRef, useState } from 'react'
import { MdAdd } from 'react-icons/md'
import todo from './Todo.module.css'
import TodoItem from './TodoItem'

export interface IList {
  id: number
  text: string | null
  done: boolean
}
const TodoCreate = () => {
  const [open, setOpen] = useState<boolean>(false)
  const [value, setValue] = useState<string>('')

  const listId = useRef<number>(0)
  let inputList: IList[] = []
  const onToggle = (e: any) => {
    e.preventDefault()
    setOpen(!open)
  }
  const onChange = () => {
    const doc = document as any
    const todo = doc.getElementById('todo')
    todo.addEventListener('keypress', onSubmit)
  }

  const onSubmit = (e: any) => {
    //input value
    if (e.key === 'Enter') {
      inputList = [
        ...inputList,
        {
          id: listId.current,
          text: todo.value,
          done: false,
        },
      ]
      listId.current += 1
      setValue('')
      console.log(inputList)
    } else {
      undefined
    }
  }

  const leftTask = useMemo(
    () => <div className={todo.task}>{inputList.length}</div>,
    [inputList]
  )

  return (
    <>
      {leftTask}
      <TodoItem inputList={inputList} />
      <div className="absolute inset-x-0 bottom-0">
        {open ? (
          <>
            <div className={todo.inputForm}>
              <input
                id="todo"
                className={todo.inputField}
                placeholder="Write what to do, and Press the Enter"
                onChange={onChange}
              />
            </div>
            <button className={todo.xBtn} onClick={onToggle}>
              <MdAdd />
            </button>
          </>
        ) : (
          <button className={todo.plusBtn} onClick={onToggle}>
            <MdAdd />
          </button>
        )}
      </div>
    </>
  )
}

export default TodoCreate
