import { MdDelete, MdDone } from 'react-icons/md'
import todo from './Todo.module.css'
import { IList } from './TodoCreate'

const TodoItem = (props: { todos: IList; onRemove: any; onToggle: any }) => {
  const { todos } = props
  const { onRemove, onToggle } = props

  return (
    <div className={todo.itemBlock}>
      {todos.done ? (
        <>
          <div className={todo.checkBtn} onClick={() => onToggle(todos.id)}>
            {todos.done && <MdDone />}
          </div>
          <div className={todo.chkedText}>{todos.text}</div>
        </>
      ) : (
        <>
          <div
            className={todo.nonCheckBtn}
            onClick={() => onToggle(todos.id)}
          ></div>
          <div className={todo.nonText}>{todos.text}</div>
        </>
      )}
      <div className={todo.remove} onClick={() => onRemove(todos.id)}>
        <MdDelete />
      </div>
    </div>
  )
}

export default TodoItem
